    private void jToggleButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jToggleButton1ActionPerformed

        
        
                        Usuario username=new Usuario();
                username.setApellido(apellido.getText());
                username.setCarnet(Integer.parseInt(carnet.getText()));
                username.setCarrera(carrera.getText());
                username.setNombre(nombre.getText());    
                username.setPassword(password.getText()); 
       
                
            if(presentacion.Login.listUsuarios.exist(Integer.parseInt(carnet.getText()))==false){    
            listUsuarios.Insertar(username);
            JOptionPane.showMessageDialog(null, "El Usuario "+carnet.getText()+" Se Guardo correctamente");
            Login loguear=new Login();
            loguear.setVisible(true);
            this.dispose();
            }else{
            JOptionPane.showMessageDialog(null, "El Usuario "+carnet.getText()+" Ya existe");
            }
            
        
        
        
        
        
        
        
        
        
        
     
        
        
        
    }//GEN-LAST:event_jToggleButton1ActionPerformed

