    private void jLabel3MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabel3MouseClicked
    
        ///////////////////////click en carga masiva
       
        try {
            String ruta="Libros.json";
            BufferedReader in = new BufferedReader(new InputStreamReader(new FileInputStream(ruta), "utf-8"));
            JSONParser parser = new JSONParser();
            String auxJson = "";
            String cadena = "";
            while ((cadena = in.readLine()) != null) {
                auxJson += cadena;
            }


            Object obj = parser.parse(auxJson);// AUX JSON YA ESTA PARSEADO 
            JSONObject jsonObject = (JSONObject) obj;// OBJETO JSON 
            JSONArray array = (JSONArray) jsonObject.get("libros");
            
  
            JOptionPane.showMessageDialog(null, "cargando datos....");

            for (Object object : array) { // RECORRIENDO EL JSON 
                JSONObject jsonObject_extraer;
                jsonObject_extraer = (JSONObject) object;
                String isbn = jsonObject_extraer.get("ISBN").toString();
                String ano= jsonObject_extraer.get("Año").toString();
                String  idioma = jsonObject_extraer.get("Idioma").toString();
                String titulo = jsonObject_extraer.get("Titulo").toString();
                String editorial = jsonObject_extraer.get("Editorial").toString();
                String autor = jsonObject_extraer.get("Autor").toString();
                String edicion = jsonObject_extraer.get("Edicion").toString();
                String categoria = jsonObject_extraer.get("Categoria").toString();
                String propietario = String.valueOf(presentacion.Login.logueado.getCarnet());
              
                
            if(presentacion.Login.logueado.getAVL().exist(categoria)){///// si encuentra la categoria ya solo inserto los libros
                
                   
             
                Libro lib=new Libro();                
                lib.setISBN(Integer.parseInt(isbn));
                lib.setAnio(Integer.parseInt(ano));
                lib.setIdioma(idioma);
                lib.setTitulo(titulo);
                lib.setEditorial(editorial);
                lib.setAutor(autor);
                lib.setEdicion(Integer.parseInt(edicion));
                lib.setCategoria(categoria);
                lib.setPropietario(propietario);
                presentacion.Login.logueado.biblio.buscar(categoria).getArbolB().insertar(Integer.parseInt(isbn), lib);
                
            
                }else{
               presentacion.Login.logueado.biblio.insertar_avl(categoria); //////////////////Sino existe la categoria la inserto para luego buscarla
                
                Libro lib=new Libro();                
                lib.setISBN(Integer.parseInt(isbn));
                lib.setAnio(Integer.parseInt(ano));
                lib.setIdioma(idioma);
                lib.setTitulo(titulo);
                lib.setEditorial(editorial);
                lib.setAutor(autor);
                lib.setEdicion(Integer.parseInt(edicion));
                lib.setCategoria(categoria);
                lib.setPropietario(propietario);
                presentacion.Login.logueado.biblio.buscar(categoria).getArbolB().insertar(Integer.parseInt(isbn), lib);
                
                          
                
               }
                
            
            
            
            
            
            
            }
            

        } catch (FileNotFoundException e) {

        } catch (IOException e) {

        } catch (ParseException e) {

        }
        
        
     
        
        
        
      
        
        
        
        
        
        
        
        
        
        
        
        
        
        
        
    }//GEN-LAST:event_jLabel3MouseClicked

